mapper.select:
SELECT task.id id , task.name name , task , writedate , userid , jobid , m_job.name jobname , limitdate , status FROM task , m_job
task.jobid = m_job.id
AND userid = #{userid}
AND jobid = #{jobid}
AND task.id = #{id}
AND task.status <> #{status}
ORDER BY writedate DESC

mapper.insert:
INSERT INTO
	task (
	  name
	, task
	, writedate
	, userid
	, jobid
	, limitdate
	, status
) VALUES (
	  #{name}
	, #{task}
	, now()
	, #{userid}
	, #{jobid}
	, #{limitdate}
	, 'op'
)

mapper.update:
UPDATE
	task
SET
	  name = #{name}
	, task = #{task}
	, writedate = now()
	, userid = #{userid}
	, jobid = #{jobid}
	, limitdate = #{limitdate}
	, status = #{status}
WHERE
	id = #{id}
